
public class Exec3_TestantoCastings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 10;
		int n2 = 2;
		int soma = n1 + n2;
		double n3 = (double) n1;
		double n4 = (double) n2;
		double div =  n3/n4;
		char A  = 'A';
		char Z  = 'Z';
		int somaletra = A + Z;
		
		System.out.println("A soma de n1 com n2 é :" + soma);
		System.out.println("A divisão de n1 com n2 é :" + div );
		System.out.println("A soma de 'A' com 'Z' é :" + somaletra );
		
	}

}
